import React from 'react';
import 'rc-color-picker/assets/index.css';
import {Button, Col, Divider, Image, Row, Select, Space, Typography} from 'antd';
import {PlusOutlined} from '@ant-design/icons';
import {CREATE_ORDER_VALUES} from "../../../constants";

const Submitting = ({
                      instructions,
                      rushService,
                      onChangeRushService,
                    }) => {
  return (
    <>
      <Row gutter={[0, 12]}>
        <Col span="24">
          <Button
            disabled={true}
            type="primary"
            icon={<PlusOutlined/>}
          >
            Upload Files ...
          </Button>
        </Col>
      </Row>
      <Divider orientation="left">Order Submitting</Divider>
      <div className="uploading__list-file">
        {instructions.map((instruction, index) => {
          return (
            <Row key={index} gutter={[0, 12]} className="b-b">
              <Col span="12">
                <Space size="large">
                  <Image
                    width={50}
                    height={50}
                    src="error"
                    fallback="data:image/png;base64,"
                  />
                  <Typography.Text strong>{instruction.name}</Typography.Text>
                </Space>
              </Col>
              <Col span="12" className="uploading-file__info">
                <div className="submitting-file__price">
                  <Typography.Text>Basic: ${instruction.basicPrice}</Typography.Text>
                  <Typography.Text>Advance: ${instruction.advancePrice}</Typography.Text>
                </div>
              </Col>
            </Row>
          )
        })}
      </div>
      <Divider orientation="left">Price</Divider>
      <Row gutter={[0, 12]} style={{marginTop: '10px'}} align="middle">
        <Col span="12">
          <div className="basic-setting__item" style={{justifyContent: 'flex-start'}}>
            <div className="basic-setting__label">Rush service</div>
            <div className="basic-setting__control" style={{marginLeft: '10px'}}>
              <Select
                value={rushService}
                onChange={(val) => onChangeRushService(val)}
                defaultValue={CREATE_ORDER_VALUES.RUSH_SERVICE[0].value}
                size="small"
                style={{width: 200}}>
                {CREATE_ORDER_VALUES.RUSH_SERVICE.map((item, index) => {
                  return (
                    <Select.Option key={index} value={item.value}>{item.text}</Select.Option>
                  )
                })}
              </Select>
            </div>
          </div>
        </Col>
        <Col span="12">
          <Space>
            <Typography.Text strong>Total:
              ${instructions.reduce((accumulator, current) => accumulator + current.basicPrice + current.advancePrice, 0)}</Typography.Text>
          </Space>
        </Col>
      </Row>
    </>
  )
}

export default Submitting;
